class CommandFunction {
    /** @param {Array<String>} commands  */
    constructor( commands ) {
        this.commands = commands;
    }

    /** @param {string} command */
    add( command ) {
        this.commands.push( command );
    }

    /** @param {Array<String>} commands */
    set( commands ) {
        this.commands = commands;
    }

    getFile() {
        let file = "";
        for ( let command of this.commands ) {
            file = file + `${command}\n`;
        }

        return file;
    }
}

module.exports = CommandFunction;