const ComponentGroup = require("./ComponentGroup");

class Entity {
    constructor(format_version,  identifier) {
        this.format_version = format_version;
        this.description = {
            identifier: identifier,
            is_spawnable: true,
            is_summonable: true
        }

        this.component_groups = {};
        this.components = {};
        this.events = {};
    }

    createComponentGroup(name) {
        const group = new ComponentGroup(name);
        this.component_groups[group.name] = group;
    }

    addComponent( component ) {
        this.components[component.name] = component.content;
    }

    addEvent( event ) {
        // code here...
    }
}