class PhysicsComponent extends Component {
    #data;

    constructor() {
        this.identifier = "minecraft:physics";
        this.#data = {
            has_collision: true,
            has_gravity: true,
        }

        // -.-.-.-
        super(identifier, this.#data);
    }

    /**
     * 
     * @param {Boolean} bool 
     */
    setCollision( bool ) {
        this.#data["has_collision"] = bool;
    }

    /**
     * 
     * @param {boolean} bool 
     */
    setGravity( bool ) {
        this.#data["has_gravity"] = bool;
    }
}